var c = require('jn-classjs');

module.exports = c.define('App.core.Controller', {
	constructor: function(app, request, response) {
		this.$app = app;
		this.$request = request;
		this.$response = response;
		(typeof this.init === 'function') && this.init();
	},

	getRequest: function() {
		return this.$request;
	},

	getResponse: function() {
		return this.$response;
	},

	getApplication: function() {
		return this.$app;
	},

	getView: function() {
		return this.$view;
	},

	setView: function(view) {
		this.$view = view;
	}
});