var Class = require('jn-classjs');

var replaceRe = /\./g;

module.exports = Class.create({
	constructor: function(libraryPath) {
		this.path = libraryPath;
	},

	require: function(name) {
		//console.log('Loading %s', name);
		name = this.path + name.replace(replaceRe, '/');
		return require(name);
	}
});