var Class = require('jn-classjs'),
	Response = require('App/Controller/Response');

module.exports = Class.create({
	constructor: function($module, request, response) {
		this.$module = $module;
		this.$request = request;
		this.$response = response;

		(typeof this.init === 'function') && this.init();
	},

	response: function(code, body, headers) {
		var response = new Response();
		response.init(code, body, headers);
		return response;
	},

	getParam: function(name, def) {
		var v = this.$request.params[name];
		return v !== undefined ? v : def;
	},

	getRequest: function() {
		return this.$request;
	},

	getResponse: function() {
		return this.$response;
	},

	getApplication: function() {
		return this.$app;
	}
});